[288] ‚îÇ &lt;exe&gt;rcise
[289] ‚îÇ &lt;exi&gt;st

Code# Match words that have not_vowel-y-not_vowel
str_view(words, "[^aeiou]y[^aeiou]")

[836] ‚îÇ &lt;sys&gt;tem
